function isMobile(mobilephone){
 //var mobilephone = ('undefined' == typeof(mobilephone) ? $("#mobilephone").val() : mobilephone);
 var num_RegExp  = /^((((1[3,5,8,7]{1}[0-9]{1}))+\d{8})(,|;| )?)*$/;
 if(mobilephone){
 if(!num_RegExp.test(mobilephone)){
 return false;
 }
 }
 return true;
}
